mapper.select:
SELECT network_id, network_type_id, network_type, network_name, network_code
network_sign, network_latitude, network_longitude, network_phone, network_mobile, network_logo, network_address, network_short_name, network_city_id, network_province_id, network_district_id, network_deleted, network_show, network_desc, network_create_time, network_update_time FROM app_network left join app_sys_province on network_province_id = province_id left join app_sys_city on network_city_id = city_id left join app_sys_district on network_district_id = district_id
AND network_deleted = 0 AND network_type = 1
AND network_name LIKE CONCAT('%',#{networkName},'%')
AND network_city_id = #{networkCityId}
AND network_province_id = #{networkProvinceId}
AND network_district_id = #{networkDistrictId}
network_id in
#{moduleIds}
ORDER BY network_distance ASC,network_create_time DESC

SELECT COUNT(1) FROM app_network left join app_sys_province on network_province_id = province_id left join app_sys_city on network_city_id = city_id left join app_sys_district on network_district_id = district_id
AND network_deleted = 0 AND network_type = 1
AND network_name LIKE CONCAT('%',#{networkName},'%')
AND network_city_id = #{networkCityId}
AND network_province_id = #{networkProvinceId}
AND network_district_id = #{networkDistrictId}
network_id in
#{moduleIds}
